import { FC } from 'react';
import Button from '../components/button';
import Stack from '../components/stack';
import Grid from '../components/grid';
import Banner from './banner';
import PFP from '../assets/logo/logo.png';
// PAGES
import About from './about';
import Roadmap from './roadmap';

export const Home: FC<{}> = () => {
  return (
    <>
      <Stack direction='VERTICAL'>
        <Banner pfp={PFP} heading='Sheesh Coin' description='An ERC20 token prioritizing decentralization &amp; blockchain tech.'>
          <Button size='L' variant="PRIMARY" as="a" href='https://app.uniswap.org/#/tokens/ethereum/0xbb4f3ad7a2cf75d8effc4f6d7bd21d95f06165ca' target="_blank">Swap Now</Button>
        </Banner>
        <Stack direction='VERTICAL' localStyles={{padding: 120, background: 'rgb(0 0 0 / 60%)', minHeight: '100vh', justifyContent: 'center'}}>
          <Grid columns={2} gap='180px' localStyles={{        
            maxWidth: 1200,
            margin: '0 auto var(--scale-48)'
          }}>
            <About />
          </Grid>
        </Stack>
        <Stack direction='VERTICAL' localStyles={{ padding: 120 }}>
          <Roadmap />
        </Stack>
      </Stack>
    </>
  );
}

export default Home;
