import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Container } from './styles';
import Home from './compositions/home';
import Portals from './compositions/portals';
import Contact from './compositions/contact';
import Navigation from './components/navigation';
import Button from './components/button';
import External from './components/icons/external';
import styled from '@emotion/styled';
import Body from './components/body';
import Twitter from './components/icons/twitter';
import Youtube from './components/icons/youtube';
import Discord from './components/icons/discord';
import Footer from './components/footer';
import Anchor from './components/anchor';
import { ScrollToTopOnRouteChange } from './utility/scroll';


const StyledLink = styled(Link)(() => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  background: 'transparent',
  border: '1px solid transparent',
  borderRadius: 4,
  margin: 0,
  textDecoration: 'none',
  '-webkit-font-smoothing': 'antialiased',
  '-moz-osx-font-smoothing': 'grayscale',
  fontFamily:  'Inter, sans-serif',
  lineHeight: 1,
  cursor: 'pointer',
  padding: '12px 16px',
  gap: 14,
}));

function App() {

  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <Router>
      <Container>

        <Navigation 
          localStyles={{position: 'fixed', top: 0}}
          isMobileMenuOpen={isMobileMenuOpen}
          toggleMobileMenu={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          <Button onClick={() => setIsMobileMenuOpen(false)} as="a" variant='PRIMARY' size='M' after={<External />} href='https://app.uniswap.org/#/tokens/ethereum/0xbb4f3ad7a2cf75d8effc4f6d7bd21d95f06165ca' target="_blank">Swap Now</Button>
        </Navigation>

        <ScrollToTopOnRouteChange />

        <Routes>
          <Route path="/portals" element={<Portals />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/" element={<Home />} />
        </Routes>

        <Footer
          contactLink='/contact'
          localStyles={{position: 'fixed', bottom: 0, left: 0}} 
          socials={
            <>
              <Anchor iconOnly href="http://t.me/ssheesherc20" target="_blank"><Discord theme="LIGHT" size="S" /></Anchor>
              <Anchor iconOnly href="https://twitter.com/sheesherc20" target="_blank"><Twitter theme="LIGHT" size="S" /></Anchor>
            </>
          } 
        />

      </Container>
    </Router>

  );
}

export default App;
