import { FC } from 'react';
import Button from '../components/button';
import Stack from '../components/stack';
import Grid from '../components/grid';
import Banner from '../components/banner';
import PFP from '../assets/logo/logo.png';
import Card from '../components/card';
import GenesisPFP from '../assets/pfp/genesis.png';

export const Home: FC<{}> = () => {



  return (
    <>

      <Stack direction='VERTICAL' localStyles={{marginTop: 0, marginBottom: 94, '@media (min-width: 1080px)': { marginBottom: 50,}}}>

        <Banner pfp={PFP} heading='Sheesh!' description='Sheesh Coin is an ERC20 token prioritizing decentralization &amp; blockchain tech.'>
          <Button size='M' variant="PRIMARY" as="a" href='https://app.uniswap.org/#/tokens/ethereum/0xbb4f3ad7a2cf75d8effc4f6d7bd21d95f06165ca' target="_blank">Swap Now</Button>
        </Banner>

        <Grid
          columns={1}
          localStyles={{
            padding: 'var(--scale-24)',
            gap: 'var(--scale-24)',
            gridTemplateColumns: '1fr',
            '@media (min-width: 600px)' :{
              padding: 'var(--scale-48)',
              gap: 'var(--scale-48)',
            },
            '@media (min-width: 800px)' :{
              gridTemplateColumns: '1fr 1fr',
            },
            '@media (min-width: 1200px)' :{
              gridTemplateColumns: '1fr 1fr 1fr 1fr',
            },
            '@media (min-width: 2000px)' :{
              gridTemplateColumns: '1fr 1fr 1fr 1fr 1fr 1fr 1fr',
            },
          }}>
          <Card heading="Genesis" description='Collection Size â€¢ 7,777' pfp={GenesisPFP} direction="VERTICAL">
            <Button as="a" size='M' variant='SECONDARY' href='https://hub.auraexchange.org/collection/Ethereum/0x2d0d57d004f82e9f4471caa8b9f8b1965a814154' target="_blank">View Collection</Button>
            <Button as="a" size='M' variant="PRIMARY" href='/portals'>View Portals</Button>
          </Card>

        </Grid>

      </Stack>
    </>
  );
}

export default Home;
