import { FC } from 'react';
import Stack from '../components/stack';
import Grid from '../components/grid';
// PAGES
import Intro from './intro';
import About from './about';
import Roadmap from './roadmap';
import Responsive from '../components/responsive';

export const Home: FC<{}> = () => {
  return (
    <>
      <Stack direction='VERTICAL'>
        <Intro />

        <Stack direction='VERTICAL' localStyles={{padding: 120, background: 'rgb(0 0 0 / 60%)', minHeight: '100vh', justifyContent: 'center'}}>


        <Responsive below={1120}>
          <Stack direction='VERTICAL' space={'var(--scale-48)'} localStyles={{        
              margin: '0 auto var(--scale-48)'
            }}>
            <About />
          </Stack>
        </Responsive>

        <Responsive above={1120}>
          <Grid columns={2} gap='180px' localStyles={{        
              maxWidth: 1200,
              margin: '0 auto var(--scale-48)'
            }}>
              <About />
          </Grid>
        </Responsive>



        </Stack>

        <Roadmap />


      </Stack>
    </>
  );
}

export default Home;
